/* toascii.cpp: This program converts integer 45 and 301
 * into ASCII characters
 */


#include <stdio.h>
#include <ctype.h>

int main()

{
   int a=217;
   char b;
   printf("before toascii () : a value =%d(%c)\n",a,a);  
   b=toascii(a);
   printf("after toascii() : a value =%d(%c)\n",b,b);
    return 0;
}
