    //添加该方法回调
    static  ECode AddStylusPressedCallback(
                    PObject pServerObj,
                     ECode (*pFunc)( PVoid, IForm *,  Int32,  Int32,  Int32,  Int64),
                     PVoid pUserData=NULL){……}
    //删除该方法回调
    static  ECode RemoveStylusPressedCallback(
                    PObject pServerObj,
                     ECode (*pFunc)( PVoid, IForm *,  Int32,  Int32,  Int32,  Int64),
                     PVoid pUserData=NULL){……}

    template <class T>
    static  ECode AddStylusPressedCallback(
                PObject pServerObj,
