example.pre:
AStringBuf_<10> asbuf;  
asbuf.Copy("a");
CConsole::WriteLine(asbuf.GetCapacity()); //Display : 10
CConsole::WriteLine(asbuf.GetLength()); //Display : 1
CConsole::WriteLine(asbuf.GetAvailableSpace()); //Display : 9

asbuf.SetEmpty();
asbuf.Copy("中");
CConsole::WriteLine(asbuf.GetCapacity()); //Display : 10
CConsole::WriteLine(asbuf.GetLength()); //Display : 2
CConsole::WriteLine(asbuf.GetAvailableSpace()); //Display : 8
