AStringBuf_<20> asbuf;
asbuf.Copy("1");
CConsole::WriteLine(asbuf.ToBoolean());  //Display : True

asbuf.SetEmpty();
asbuf.Copy("0");
CConsole::WriteLine(asbuf.ToBoolean());  //Display : False
	
asbuf.SetEmpty();
asbuf.Copy("10");	
CConsole::WriteLine(asbuf.ToBoolean());  //Display : False
