example.pre:
AStringBuf_<20> asbuf;
asbuf.Copy(("-1"));
CConsole::WriteLine(asbuf.ToInt32());  //Display : -1

asbuf.SetEmpty();
asbuf.Copy("abc");
CConsole::WriteLine(asbuf.ToInt32());  //Display : 0
	
asbuf.SetEmpty();
asbuf.Copy("123abc");	
CConsole::WriteLine(asbuf.ToInt32());  //Display : 123

asbuf.SetEmpty();
asbuf.Copy("abc123");	
CConsole::WriteLine(asbuf.ToInt32());  //Display : 0
