example.pre:
char pBuf1[10]={'s','s','d','0'};
BufferOf<char> *pArray = BufferOf<char>::Alloc(pBuf1,20);

(*pArray).Append('s');
(*pArray).Append('s');
(*pArray).Append('d');
(*pArray).Append('0');

CConsole::WriteLine((*pArray).GetCapacity());  //Display : 20
CConsole::WriteLine((*pArray).GetAvailableSpace());  //Display : 0

char *pch=(*pArray).GetPayload();

CConsole::WriteLine(pch[0]);  //Display : 's'
CConsole::WriteLine(pch[1]);  //Display : 's'
CConsole::WriteLine(pch[2]);  //Display : 'd'
CConsole::WriteLine(pch[3]);  //Display : '0'

BufferOf<char>::Free(pArray);
