example.pre:
U8StringBuf_<10> asbuf;
Int8 value1 = -1;
asbuf.Append(value1);
CConsole::WriteLine(asbuf.GetLength());  //Display : 2
CConsole::WriteLine(asbuf);  //Display : -1

asbuf.SetEmpty();

Int8 value2 = 1;
asbuf.Append(value2);
CConsole::WriteLine(asbuf.GetLength());  //Display : 1
CConsole::WriteLine(asbuf);  //Display : 1

asbuf.SetEmpty();

asbuf.Append(value2, MakeNumberFormat(NumberFormat_Decimal | NumberFormat_RightAlign, 4, 3));
CConsole::WriteLine(asbuf.GetLength());  //Display : 4
CConsole::Write("|");
CConsole::Write(asbuf);
CConsole::WriteLine("|");  //Display : | 001|
