example.pre:
U8StringBuf_<10> u8sbuf;  
u8sbuf.Copy(L"中");
CConsole::WriteLine(u8sbuf.GetLength());  //Display : 3
CConsole::WriteLine(u8sbuf.EstimateWStringLength());  //Display : 1

u8sbuf.SetEmpty();
u8sbuf.Copy(L"a");
CConsole::WriteLine(u8sbuf.GetLength());  //Display : 1
CConsole::WriteLine(u8sbuf.EstimateWStringLength());  //Display : 1
