example.pre:
U8StringBuf_<20> u8sbuf;
u8sbuf.Copy("123456");
CConsole::WriteLine(u8sbuf.GetCharCount());  //Display : 6
CConsole::WriteLine(u8sbuf.GetCharCount(Encoding_ASCII));  //Display : 6
CConsole::WriteLine(u8sbuf.GetCharCount(Encoding_UTF7));  //Display : 5
CConsole::WriteLine(u8sbuf.GetCharCount(Encoding_UTF8));  //Display : 7
CConsole::WriteLine(u8sbuf.GetCharCount(Encoding_UTF16));  //Display : 2
CConsole::WriteLine(u8sbuf.GetCharCount(Encoding_GB18030));  //Display : 6 

U8StringBuf_<20> u8sbuf1;
u8sbuf1.Copy(L"dd中d");
CConsole::WriteLine(u8sbuf1.GetCharCount());  //Display : 5
