/* isprint.cpp: This program uses isprint to verify c
 * whether it is a printable character.
 */

#include <stdio.h>
#include <ctype.h>

int main()
{
    int c = 100;
    if (isprint(c)) {
        printf("it is a printable character.\n");
    }
    else {
        printf("it is not a printable character.\n");
    }

    return 0;
}
