example.pre:
#include <stdio.h>
#include <ezarray.h>

int __cdecl main()
{
    EzArray<int> myArray = EzArray<int>::CreateObject(10);

    if (!myArray.IsNull()) {
        printf("myArray's value is not NULL.");
    }
    else {
        printf("myArray's value is NULL.");
    }
    myArray.Dispose();
    return 0;
}
