example.pre:
#include <stdio.h>
#include <ezbuf.h>

int __cdecl main()
{
    EzByteBuf_<200> buf;
    int old_used, new_used;

    buf.Copy("Good luck!", 11);
    old_used = buf.Used();

    buf.SetUsed(20);
    new_used = buf.Used();

    if (!buf.IsNull()) {
        printf("'%s' original used %d byte,\n", (char*)buf, old_used);
        printf("After setused 20, now used %d bytes.\n", new_used);
    } else {
        printf("wrong EzByteBuf!");
    }


    return 0;
}
