example.pre:
#include <stdio.h>
#include <ezstr.h>

void __cdecl main()
{
    EzByteBuf_<50> buf1;
    EzByteBuf_<50> buf2;

    buf1.Copy("Hello,world!", 13);
    buf2.Copy("World,hello!", 13);

    printf("Before change,buf1 is \"%s\".\n",(char*)buf1);
    buf1 = buf2;
    printf("After change,buf1 is \"%s\".\n",(char*)buf1);
}
