example.pre:
#include <stdio.h>
#include <ezstr.h>

int __cdecl main()
{
    EzStr str1 = EzStr::AllocString(L"Hello, world!");

    printf("Before change,str1 is: %S\n", (wchar_t*)str1);
    ECODE ec = EzStr::ReAllocStringLen(str1, L"Now,world,hello!", 15);
    if (!FAILED(ec)) {
        printf("call ReAllocStringLen (cch=15),str1 is: %S\n", (wchar_t*)str1);
    }
    else {
        printf("ReAllocString failed!\n");
    }
    EzStr::FreeString(str1);

    return 0;
}
