example.pre:
#include <stdio.h>
#include <ezstr.h>

int __cdecl main()
{
    EzStr str1 = EZCSTR("Hello,world!");
    EzStr str2 = EZCSTR("World,hello!");

    printf("Before change,str1 is \"%S\".\n", (wchar_t*)str1);
    str1 = str2;
    printf("After change,str1 is \"%S\".\n", (wchar_t*)str1);

    return 0;
}
