example.pre:
#include <stdio.h>
#include <ezbuf.h>
#include <string.h>

int __cdecl main()
{
    wchar_t * buf1 = L"Hello world!";

    EzWCharBuf_Box myBuf1(buf1, 12, 12);

    wprintf(L"mybuf1:%s\n", (wchar_t*)myBuf1);
    wprintf(L"mybuf1 Capacity:%d\n", myBuf1.Capacity());
    wprintf(L"mybuf1 used:%d\n", myBuf1.Used());

    wchar_t * buf2 = new wchar_t[30];
    wcscpy(buf2, L"Hello EzWCharBuf_Box!");

    EzWCharBuf_Box myBuf2(buf2, 30, wcslen(buf2));
    wprintf(L"myBuf2:%s\n", (wchar_t*)myBuf2);
    wprintf(L"myBuf2 Capacity:%d\n", myBuf2.Capacity());
    wprintf(L"myBuf2 used:%d\n", myBuf2.Used());

    delete buf2;

    return 0;
}
