example.pre:
#include <stdio.h>
#include <ezbuf.h>

int __cdecl main()
{
    EzWCharBuf_<200> buf;

    buf.Copy(L"Good to you!",13);
    wprintf(L"Original: %s\n",(wchar_t*)buf);

    buf.Insert(L"luck ",5,5);
    wprintf(L"After inserting at pos=5: %s\n",(wchar_t*)buf);

    return 0;
}
